    Departement dept = new Departement();
    
    private void Clear_Data()
    {
        Tools.ClearText(this);
        Txt_DeptNO.setText(dept.Get_Auto_Number());
        
        Btn_Add.setEnabled(true);
        Btn_Update.setEnabled(false);
        Btn_Delete.setEnabled(false);
        
        Txt_DeptName.requestFocus();
    }
//--------------------------------------------------------------------------------------
    private void Set_Values()
    {
        int D_NO = Integer.parseInt(Txt_DeptNO.getText());
        String D_Name = Txt_DeptName.getText();
        String D_Location = Txt_DeptLocation.getText();
        

        dept.setDept_NO(D_NO);
        dept.setDept_Name(D_Name);
        dept.setLocation(D_Location);
        
        
    }    
    
    private void Btn_ClearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_ClearActionPerformed
         Clear_Data();
    }//GEN-LAST:event_Btn_ClearActionPerformed

