    private void Btn_AddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_AddActionPerformed
        
        Set_Values();
        dept.Add();
        Clear_Data();
        dept.Get_All_Rows(Table_Dept);
        
    }//GEN-LAST:event_Btn_AddActionPerformed

    private void Btn_UpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_UpdateActionPerformed
     
        Set_Values();
        dept.Update();
        Clear_Data();
        dept.Get_All_Rows(Table_Dept);
        
    }//GEN-LAST:event_Btn_UpdateActionPerformed

    private void Btn_DeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_DeleteActionPerformed
        if(Tools.Confirm_Msg(" Voulez vous vraiment supprimer le Département ?! "))
        {    
            Set_Values();
            dept.Delete();
            Clear_Data();
            dept.Get_All_Rows(Table_Dept);
        }
    }//GEN-LAST:event_Btn_DeleteActionPerformed

    private void Btn_FindActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_FindActionPerformed
        
        String Str_DeptNO = (String)Tools.InputBox(" Veillez saisir le numéro du Département !");
        int DeptNO = Integer.parseInt(Str_DeptNO);
        
        dept.setDept_NO(DeptNO);
        dept.Get_One_Row(Table_Dept);
        
    }//GEN-LAST:event_Btn_FindActionPerformed

    private void Btn_AllActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_AllActionPerformed
        
        dept.Get_All_Rows(Table_Dept);
        
    }//GEN-LAST:event_Btn_AllActionPerformed

    private void Table_DeptMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Table_DeptMouseClicked
        try
        {
            int row = Table_Dept.getSelectedRow();
            
            String Str_Number =Table_Dept.getValueAt(row, 0).toString();
            String Str_Name =Table_Dept.getValueAt(row, 1).toString();
            String Str_Local =Table_Dept.getValueAt(row, 2).toString();
            
            Txt_DeptNO.setText(Str_Number);
            Txt_DeptName.setText(Str_Name);
            Txt_DeptLocation.setText(Str_Local);
            
            Btn_Add.setEnabled(false);
            Btn_Update.setEnabled(true);
            Btn_Delete.setEnabled(true);
            
        }
        catch(Exception ex)
        {
            Tools.MsgBox(ex.getMessage());
        }
    }//GEN-LAST:event_Table_DeptMouseClicked

    private void Btn_SearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_SearchActionPerformed
        
        String Str_Search = "select * from departement_data where " ;
        
        if(Rdo_Number.isSelected())
        {
            Str_Search += "Departement_NO";
        }
        else if(Rdo_Name.isSelected())
        {
            Str_Search += "Departement";
        }
        else 
        {
            Str_Search += "Location";
        }
        
        Str_Search += " like '%" + Txt_Search.getText() +  "%' ;";
        
        dept.Get_Custom_Rows(Str_Search, Table_Dept);
        
        
    }//GEN-LAST:event_Btn_SearchActionPerformed

