    private void Table_Work_OnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Table_Work_OnMouseClicked

        try
        {
            int row = Table_Work_On.getSelectedRow();

            String Str_EmpName =Table_Work_On.getValueAt(row, 1).toString();
            String Str_ProjectName =Table_Work_On.getValueAt(row, 3).toString();
            

            Combo_Emp.setSelectedItem(Str_EmpName);
            Combo_Project.setSelectedItem(Str_ProjectName);


        }
        catch(Exception ex)
        {
            Tools.MsgBox(ex.getMessage());
        }

    }//GEN-LAST:event_Table_Work_OnMouseClicked

