    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
       
        data_base.Go.Fill_Combo("Employee", "EmpName", Combo_Emp);
        data_base.Go.Fill_Combo("Project", "ProjectName", Combo_Project);
        
         Btn_Group.add(Rdo_Num_Emp);
         Btn_Group.add(Rdo_Name_Emp);
         Btn_Group.add(Rdo_Num_Project);
         Btn_Group.add(Rdo_Name_Project);
         
         wOn.Get_All_Rows(Table_Work_On);
         
         Table_Work_On.getTableHeader().setReorderingAllowed(false);
         

    }//GEN-LAST:event_formWindowOpened

